import React from 'react';

// Official PGE-SP Logo as a Base64 encoded data URI
const logoSrc = "data:image/svg+xml;base64,";

interface LogoProps {
  className?: string;
}

const Logo: React.FC<LogoProps> = ({ className }) => {
  return (
    <img src={logoSrc} alt="PGE-SP Logo" className={className} />
  );
};

export default Logo;
